import styled from 'styled-components';

export const Text = styled.div`
    color: red;
    font-size:  ${({ isActive }) => isActive ? '1px solid black' : '3px dotted green'};
    border: ${({ isActive }) => isActive ? '1px solid black' : '3px dotted green'}
`
